tables.table_schema:
CREATE TABLE UserLocationARInformation (
    user_id INT PRIMARY KEY,
    user_name STRING,
    user_location_geo GEOGRAPHY,
    user_ar_environment JSON
);

CREATE TABLE ar_user_locations (
    user_id INT PRIMARY KEY,
    user_location GEOGRAPHY
);

CREATE TABLE ar_information (
    information_id INT PRIMARY KEY,
    user_id INT,
    information TEXT
);

CREATE TABLE user_location (
    user_id UUID PRIMARY KEY,
    user_name VARCHAR(255),
    user_location POINT
);

CREATE TABLE ar_environment (
    environment_id UUID PRIMARY KEY,
    environment_name VARCHAR(255),
    environment_description TEXT
);

CREATE TABLE user_ar_interaction (
    interaction_id UUID PRIMARY KEY,
    user_id UUID REFERENCES user_location(user_id),
    environment_id UUID REFERENCES ar_environment(environment_id),
    interaction_date TIMESTAMP
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    location VARCHAR(50),
    ar_enabled BOOLEAN
);

CREATE TABLE locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(50),
    latitude DECIMAL(9,6),
    longitude DECIMAL(9,6)
);

CREATE TABLE ar_objects (
    object_id INT PRIMARY KEY,
    object_name VARCHAR(50),
    location_id INT,
    FOREIGN KEY (location_id) REFERENCES locations(location_id)
);

CREATE TABLE UserLocations (
  user_id INT PRIMARY KEY,
  user_name VARCHAR(50),
  location_lat FLOAT,
  location_long FLOAT
);

CREATE TABLE UserDevice (
  device_id INT PRIMARY KEY,
  user_id INT,
  device_type VARCHAR(50),
  device_os VARCHAR(50)
);

CREATE TABLE UserPreferences (
  user_id INT PRIMARY KEY,
  theme_color VARCHAR(20),
  font_size INT
);

CREATE TABLE UserActions (
  action_id INT PRIMARY KEY,
  user_id INT,
  action_type VARCHAR(50),
  timestamp TIMESTAMP
);
