tables.table_schema:
CREATE TABLE virtual_objects (
    id INT PRIMARY KEY,
    object_name VARCHAR(50),
    object_type VARCHAR(50),
    quantity INT
);

CREATE TABLE ar_scenes (
    id INT PRIMARY KEY,
    scene_name VARCHAR(50),
    scene_description TEXT,
    object_id INT,
    FOREIGN KEY (object_id) REFERENCES virtual_objects(id)
);

CREATE TABLE virtual_objects (
    id INT PRIMARY KEY,
    object_name VARCHAR(255),
    category VARCHAR(50),
    description TEXT
);

CREATE TABLE scenes (
    scene_id INT PRIMARY KEY,
    location VARCHAR(100),
    date_added DATE
);

CREATE TABLE ARScenes (
  id SERIAL PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  location VARCHAR(100) NOT NULL,
  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE Users (
  id SERIAL PRIMARY KEY,
  username VARCHAR(50) UNIQUE NOT NULL,
  email VARCHAR(100) UNIQUE NOT NULL,
  password VARCHAR(100) NOT NULL
);

CREATE TABLE user_roles (
    user_id UUID PRIMARY KEY,
    role_name VARCHAR(50)
);

CREATE TABLE scene_permissions (
    permission_id UUID PRIMARY KEY,
    scene_id UUID,
    user_id UUID,
    permission_type VARCHAR(20)
);
