tables.table_schema:
CREATE TABLE virtual_characters (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    behavior VARCHAR(100) NOT NULL
);

CREATE TABLE ar_queries (
    query_id SERIAL PRIMARY KEY,
    character_id INT,
    query_text TEXT
);

CREATE TABLE character_data (
    character_id INT PRIMARY KEY,
    character_name VARCHAR(50) NOT NULL,
    ar_experience INT,
    is_active BOOLEAN
);

CREATE TABLE ARDevices (
    device_id UUID PRIMARY KEY,
    device_name VARCHAR(50),
    device_type VARCHAR(20),
    owner_id UUID
);

CREATE TABLE CharacterInventory (
    inventory_id UUID PRIMARY KEY,
    character_id UUID REFERENCES VirtualCharacter(character_id),
    item_name VARCHAR(50),
    quantity INT
);

CREATE TABLE Character (
    character_id UUID PRIMARY KEY,
    character_name VARCHAR(50) NOT NULL,
    character_type VARCHAR(20) NOT NULL
);

CREATE TABLE Location (
    location_id UUID PRIMARY KEY,
    character_id UUID REFERENCES Character(character_id),
    location_name VARCHAR(50) NOT NULL,
    latitude DECIMAL(10, 6) NOT NULL,
    longitude DECIMAL(10, 6) NOT NULL
);

CREATE TABLE Action (
    action_id UUID PRIMARY KEY,
    character_id UUID REFERENCES Character(character_id),
    action_name VARCHAR(50) NOT NULL,
    action_type VARCHAR(20) NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE Inventory (
    inventory_id UUID PRIMARY KEY,
    character_id UUID REFERENCES Character(character_id),
    item_name VARCHAR(50) NOT NULL,
    quantity INTEGER NOT NULL
);

CREATE TABLE Behavior (
    behavior_id UUID PRIMARY KEY,
    character_id UUID REFERENCES Character(character_id),
    behavior_type VARCHAR(50) NOT NULL,
    description TEXT,
    approval_status BOOLEAN NOT NULL
);
