tables.table_schema:
CREATE TABLE geofences (
   id INT PRIMARY KEY,
   name VARCHAR(255),
   location GEOGRAPHY
);

CREATE TABLE devices (
   id INT PRIMARY KEY,
   name VARCHAR(255),
   current_location GEOGRAPHY
);

CREATE TABLE notifications (
   id INT PRIMARY KEY,
   device_id INT,
   notification_text TEXT,
   timestamp TIMESTAMP
);

CREATE TABLE Geofences (
    geofence_id INT PRIMARY KEY,
    geofence_name TEXT,
    geofence_coordinates TEXT
);

CREATE TABLE Devices (
    device_id INT PRIMARY KEY,
    device_name TEXT,
    device_status TEXT
);

CREATE TABLE GeofenceLogs (
    log_id INT PRIMARY KEY,
    device_id INT,
    geofence_id INT,
    log_timestamp TIMESTAMP
);

CREATE TABLE Notifications (
    notification_id INT PRIMARY KEY,
    log_id INT,
    notification_type TEXT,
    notification_message TEXT
);
