tables.table_schema:
CREATE TABLE geocoded_addresses (
    geocode_id INT PRIMARY KEY,
    user_id INT,
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE user_locations (
    location_id UUID,
    user_id UUID,
    address_id UUID,
    geocoding_id UUID
);

CREATE TABLE map_markers (
    marker_id UUID,
    user_id UUID,
    location_id UUID,
    marker_name TEXT
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    address VARCHAR(100) NOT NULL
);

CREATE TABLE locations (
    location_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users,
    latitude DOUBLE PRECISION NOT NULL,
    longitude DOUBLE PRECISION NOT NULL
);

CREATE TABLE geocoding_data (
    geocode_id UUID PRIMARY KEY,
    location_id UUID REFERENCES locations,
    geocoding_status VARCHAR(20) NOT NULL,
    geocoding_result JSON
);

CREATE TABLE maps (
    map_id UUID PRIMARY KEY,
    map_name VARCHAR(50) NOT NULL,
    map_url VARCHAR(200) NOT NULL
);

CREATE TABLE user_stats (
    stat_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users,
    total_locations INT NOT NULL,
    last_updated TIMESTAMP
);
