tables.table_schema:
CREATE TABLE HeatMapData (
  region_id INT,
  region_name VARCHAR(50),
  population_density FLOAT,
  crime_rate FLOAT
);

CREATE TABLE heat_map_data (
  region_id INT PRIMARY KEY,
  region_name VARCHAR(50),
  population_density FLOAT
);

CREATE TABLE PopulationDensity (
  region_id INT,
  region_name TEXT,
  population INT
);

CREATE TABLE CrimeRates (
  region_id INT,
  region_name TEXT,
  crime_rate FLOAT
);

CREATE TABLE Regions (
  region_id INT,
  region_name TEXT,
  latitude FLOAT,
  longitude FLOAT
);
