tables.table_schema:
CREATE TABLE drone_flight_paths (
    flight_id INT PRIMARY KEY,
    start_location VARCHAR,
    end_location VARCHAR,
    flight_time TIMESTAMP
);

CREATE TABLE drone_paths (
    id INT PRIMARY KEY,
    path_name VARCHAR(50),
    start_location VARCHAR(100),
    end_location VARCHAR(100)
);

CREATE TABLE map_data (
    id INT PRIMARY KEY,
    map_id INT,
    map_name VARCHAR(50),
    coordinates VARCHAR(255)
);

CREATE TABLE drone_flight_paths (
    flight_id INT PRIMARY KEY,
    drone_id INT,
    path_data JSONB
);

CREATE TABLE drone_devices (
  drone_id INT PRIMARY KEY,
  model VARCHAR(50),
  manufacturer VARCHAR(50),
  purchase_date DATE
);

CREATE TABLE map_coordinates (
  map_id INT PRIMARY KEY,
  latitude DOUBLE,
  longitude DOUBLE
);
