tables.table_schema:
CREATE TABLE geofencing_notifications (
    id UUID PRIMARY KEY,
    notification_name STRING,
    notification_message STRING,
    geofence_polygon GEOMETRY(POLYGON)
);

CREATE TABLE geofence_locations (
    geofence_id INT PRIMARY KEY,
    geofence_name TEXT,
    geofence_area GEOGRAPHY,
    alert_radius INT
);

CREATE TABLE user_locations (
    user_id INT PRIMARY KEY,
    user_name TEXT,
    user_location GEOGRAPHY,
    last_updated TIMESTAMP
);

CREATE TABLE geofences (
    id INT PRIMARY KEY,
    name TEXT,
    boundary GEOGRAPHY
);

CREATE TABLE locations (
    id INT PRIMARY KEY,
    name TEXT,
    coordinates GEOGRAPHY
);

CREATE TABLE notifications (
    id INT PRIMARY KEY,
    location_id INT,
    message TEXT
);

CREATE TABLE geofence_areas (
 id INT PRIMARY KEY,
 area_name TEXT,
 geo_polygon GEOGRAPHY(MULTIPOLYGON)
);

CREATE TABLE users (
 user_id INT PRIMARY KEY,
 username TEXT,
 email TEXT
);

CREATE TABLE user_locations (
 location_id INT PRIMARY KEY,
 user_id INT,
 location GEOGRAPHY(POINT),
 timestamp TIMESTAMP
);
