tables.table_schema:
CREATE TABLE historical_traffic_data (
  id INT PRIMARY KEY,
  route_id INT,
  traffic_density FLOAT,
  historical_time TIMESTAMP
);

CREATE TABLE routes (
  route_id INT PRIMARY KEY,
  route_name VARCHAR,
  start_location VARCHAR,
  end_location VARCHAR
);

CREATE TABLE route_optimization (
    id SERIAL PRIMARY KEY,
    route_id INT REFERENCES traffic_patterns(id),
    optimization_strategy VARCHAR(255),
    optimized_route VARCHAR(255)
);

CREATE TABLE TrafficPatterns (
    pattern_id INT PRIMARY KEY,
    pattern_name VARCHAR(50) NOT NULL,
    traffic_intensity INT NOT NULL,
    traffic_duration TIME
);

CREATE TABLE MapLocations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(100) NOT NULL,
    latitude DECIMAL(10, 8) NOT NULL,
    longitude DECIMAL(11, 8) NOT NULL
);

CREATE TABLE UserPreferences (
    user_id INT PRIMARY KEY,
    preferred_route_id INT REFERENCES RouteOptimizationSystem(route_id),
    preferred_start_location VARCHAR(100) NOT NULL,
    preferred_end_location VARCHAR(100) NOT NULL
);
