tables.table_schema:
CREATE TABLE LocationUpdates (
  id INT PRIMARY KEY,
  location_id INT,
  timestamp TIMESTAMP,
  geofence_id INT
);

CREATE TABLE location_updates (
  location_id UUID PRIMARY KEY,
  timestamp TIMESTAMP,
  geofenced_region VARCHAR
);

CREATE TABLE dwell_times (
  dwell_id UUID PRIMARY KEY,
  location_id UUID REFERENCES location_updates(location_id),
  start_time TIMESTAMP,
  end_time TIMESTAMP
);

CREATE TABLE location_updates (
    location_id INT PRIMARY KEY,
    latitude FLOAT,
    longitude FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE geofenced_regions (
    region_id INT PRIMARY KEY,
    region_name VARCHAR(50),
    region_polygon GEOMETRY
);

CREATE TABLE analysis_results (
    analysis_id INT PRIMARY KEY,
    region_id INT,
    avg_dwell_time INTERVAL
);

CREATE TABLE location_updates (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(255),
    timestamp TIMESTAMP
);

