tables.table_schema:
CREATE TABLE weather_data (
  weather_id INT PRIMARY KEY,
  temperature FLOAT,
  humidity FLOAT,
  wind_speed FLOAT
);

CREATE TABLE geographical_data (
  location_id INT PRIMARY KEY,
  latitude FLOAT,
  longitude FLOAT,
  altitude FLOAT
);

CREATE TABLE agriculture_data (
  record_id INT PRIMARY KEY,
  crop_name VARCHAR(255),
  crop_yield FLOAT,
  soil_type VARCHAR(255)
);

CREATE TABLE CropDetails (
	crop_name VARCHAR(255),
	planting_season VARCHAR(255),
	harvest_season VARCHAR(255),
	PRIMARY KEY (crop_name)
);

CREATE TABLE FarmInformation (
	farm_id SERIAL PRIMARY KEY,
	owner_name VARCHAR(255),
	city_name VARCHAR(255)
);
