tables.table_schema:
CREATE TABLE TrafficData ( 
    timestamp TIMESTAMP, 
    route_id INT, 
    traffic_speed INT, 
    congestion_level INT 
);

CREATE TABLE real_time_traffic_data (
    id INT PRIMARY KEY,
    route_name VARCHAR(100),
    current_speed FLOAT,
    traffic_conditions VARCHAR(50)
);

CREATE TABLE congestion_analysis (
    id INT PRIMARY KEY,
    route_id INT,
    congestion_level VARCHAR(50),
    delay_time INT
);

CREATE TABLE Routes (
    route_id INT PRIMARY KEY,
    route_name VARCHAR(255),
    start_location VARCHAR(255),
    end_location VARCHAR(255)
);

CREATE TABLE TrafficCameras (
    camera_id INT PRIMARY KEY,
    route_id INT,
    location VARCHAR(255),
    camera_type ENUM('fixed', 'mobile')
);
