tables.table_schema:
CREATE TABLE satellite_metadata (
  satellite_id INT PRIMARY KEY,
  satellite_name VARCHAR(255),
  launch_date DATE,
  status VARCHAR(50)
);

CREATE TABLE satellite_teams ( 
    team_id UUID PRIMARY KEY, 
    team_name VARCHAR(100), 
    number_of_members INT, 
    team_lead VARCHAR(100) 
);

CREATE TABLE Satellite (
  satellite_id SERIAL PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  launch_date DATE NOT NULL,
  orbital_slot VARCHAR(100) NOT NULL
);

CREATE TABLE GroundStation (
  station_id SERIAL PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  location VARCHAR(100) NOT NULL
);

CREATE TABLE TrackingStation {
  id INT PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  location VARCHAR(255) NOT NULL,
  capacity INT
}
