tables.table_schema:
CREATE TABLE satellites ( 
  satellite_id INT PRIMARY KEY, 
  launch_date DATE, 
  trajectory_pattern VARCHAR(255) 
);

CREATE TABLE User (user_id INT PRIMARY KEY,
 username VARCHAR,
 email VARCHAR UNIQUE,
 registration_date DATE,
 last_login DATE
);

CREATE TABLE QueryHistory (query_id INT PRIMARY KEY,
 user_id INT,
 query_text TEXT,
 query_date DATETIME,
 CONSTRAINT fk_user_id FOREIGN KEY (user_id) REFERENCES User(user_id)
);
