tables.table_schema:
CREATE TABLE satellite_tracking_data (
    satellite_id INT,
    timestamp TIMESTAMP,
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE satellite_tracking_data (
    satellite_id INT PRIMARY KEY,
    location VARCHAR(50),
    tracking_time TIMESTAMP
);

CREATE TABLE weather_information (
    location VARCHAR(50) PRIMARY KEY,
    temperature INT,
    weather_type VARCHAR(50)
);

CREATE TABLE satellite_data (
    satellite_id INT PRIMARY KEY,
    location VARCHAR,
    timestamp TIMESTAMP,
    temperature FLOAT
);

CREATE TABLE weather_data (
    location VARCHAR PRIMARY KEY,
    temperature FLOAT,
    humidity FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE tracking_data (
    satellite_id INT,
    location VARCHAR,
    timestamp TIMESTAMP,
    altitude FLOAT
);

CREATE TABLE event_log (
    event_id INT PRIMARY KEY,
    event_type VARCHAR,
    event_description TEXT,
    event_timestamp TIMESTAMP
);
