tables.table_schema:
CREATE TABLE satellite_data (
    satellite_id UUID PRIMARY KEY,
    altitude FLOAT
);

CREATE TABLE report (
    report_id UUID PRIMARY KEY,
    satellite_id UUID,
    date TIMESTAMP,
    change_amount FLOAT
);

CREATE TABLE SatelliteData (
    satellite_id INT,
    altitude FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE ReportOverview (
    report_id INT,
    report_name VARCHAR,
    creation_date DATE
);

CREATE TABLE AltitudeChange (
    change_id INT,
    satellite_id INT,
    change_amount FLOAT,
    change_date DATE
);

CREATE TABLE satellite (satellite_id INT PRIMARY KEY,
    satellite_name VARCHAR(255),
    altitude FLOAT);

CREATE TABLE reports (report_id INT PRIMARY KEY,
    report_date DATE,
    satellite_id INT);

CREATE TABLE Satellites (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  altitude FLOAT
);

CREATE TABLE AltitudeChanges (
  id INT PRIMARY KEY,
  satellite_id INT REFERENCES Satellites(id),
  timestamp TIMESTAMP,
  change FLOAT
);

CREATE TABLE AggregateData (
  id INT PRIMARY KEY,
  report_id INT REFERENCES ReportGeneration(id),
  average_altitude FLOAT,
  total_changes FLOAT
);
