tables.table_schema:
CREATE TABLE satellites (
    satellite_id INT PRIMARY KEY,
    satellite_name VARCHAR(255),
    health_status BOOLEAN,
    performance_metrics JSON
);

CREATE TABLE SatelliteMetrics (
    satellite_id UUID PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    health_status VARCHAR(10) NOT NULL,
    battery_level FLOAT NOT NULL
);

CREATE TABLE SatelliteLogs (
    log_id SERIAL PRIMARY KEY,
    satellite_id UUID NOT NULL,
    log_timestamp TIMESTAMP NOT NULL,
    log_message TEXT
);

CREATE TABLE SatelliteLocations (
    location_id SERIAL PRIMARY KEY,
    satellite_id UUID NOT NULL,
    latitude FLOAT NOT NULL,
    longitude FLOAT NOT NULL
);

CREATE TABLE satellites (
    satellite_id UUID PRIMARY KEY,
    satellite_name VARCHAR(50) NOT NULL,
    health_status VARCHAR(20),
    performance_metrics JSONB
);

CREATE TABLE satellite_data (
    satellite_id INT PRIMARY KEY,
    satellite_name VARCHAR(255) NOT NULL,
    health_status VARCHAR(50),
    performance_metric FLOAT
 );

CREATE TABLE satellite_logs (
    log_id INT PRIMARY KEY,
    satellite_id INT,
    log_timestamp TIMESTAMP,
    log_message TEXT
 );

CREATE TABLE satellite_location (
    location_id INT PRIMARY KEY,
    satellite_id INT,
    location_lat DECIMAL(9, 6),
    location_long DECIMAL(9, 6)
 );

CREATE TABLE satellite_errors (
    error_id INT PRIMARY KEY,
    satellite_id INT,
    error_code VARCHAR(10),
    error_description TEXT
 );

CREATE TABLE satellite_commands (
    command_id INT PRIMARY KEY,
    satellite_id INT,
    command_type VARCHAR(50),
    command_status VARCHAR(20)
 );
