tables.table_schema:
CREATE TABLE satellite_logs (
  log_id SERIAL PRIMARY KEY,
  satellite_id INT,
  timestamp TIMESTAMP,
  message TEXT
);

CREATE TABLE satellite_status (
  status_id SERIAL PRIMARY KEY,
  satellite_id INT,
  status TEXT,
  last_check TIMESTAMP
);

CREATE TABLE satellites (
    satellite_id INT PRIMARY KEY,
    satellite_name VARCHAR(255),
    launch_date DATE
);

CREATE TABLE communication_logs (
    log_id INT PRIMARY KEY,
    satellite_id INT,
    log_time TIMESTAMP,
    message TEXT
);

CREATE TABLE troubleshooting_issues (
    issue_id INT PRIMARY KEY,
    log_id INT,
    issue_description TEXT,
    resolution TEXT
);

CREATE TABLE satellites (
    id INT PRIMARY KEY,
    name TEXT NOT NULL,
    location TEXT,
    last_communication TIMESTAMP
);

CREATE TABLE communication_logs (
    log_id INT PRIMARY KEY,
    satellite_id INT REFERENCES satellites(id),
    timestamp TIMESTAMP,
    message TEXT
);

CREATE TABLE errors (
    error_id INT PRIMARY KEY,
    log_id INT REFERENCES communication_logs(log_id),
    error_type TEXT,
    description TEXT
);

CREATE TABLE technicians (
    technician_id INT PRIMARY KEY,
    name TEXT NOT NULL,
    specialization TEXT
);

CREATE TABLE troubleshooting_tasks (
    task_id INT PRIMARY KEY,
    error_id INT REFERENCES errors(error_id),
    assigned_technician INT REFERENCES technicians(technician_id),
    status TEXT
);
