tables.table_schema:
CREATE TABLE satellite_meta_data (
    satellite_id INT PRIMARY KEY,
    satellite_name VARCHAR(50),
    launch_date DATE,
    operator VARCHAR(50)
);

CREATE TABLE satellites (
    satellite_id UUID PRIMARY KEY,
    satellite_name VARCHAR(255) NOT NULL,
    launch_date DATE NOT NULL,
    last_tracking_date TIMESTAMP
);

CREATE TABLE SatellitePositions (
  satellite_id INT,
  timestamp TIMESTAMP,
  latitude FLOAT,
  longitude FLOAT
);

CREATE TABLE TrackingData (
  tracking_id INT,
  satellite_id INT,
  latency FLOAT,
  signal_strength FLOAT
);

CREATE TABLE OrbitalParameters (
  satellite_id INT,
  epoch TIMESTAMP,
  inclination FLOAT,
  eccentricity FLOAT
);

CREATE TABLE SatelliteNames (
  satellite_id INT,
  satellite_name VARCHAR
);
