tables.table_schema:
CREATE TABLE locations (
  location_id UUID PRIMARY KEY,
  longitude FLOAT,
  latitude FLOAT,
  timestamp TIMESTAMPTZ
);

CREATE TABLE geofences (
  geofence_id UUID PRIMARY KEY,
  center_longitude FLOAT,
  center_latitude FLOAT,
  radius INT
);

CREATE TABLE historical_location_data (
    location_id INT PRIMARY KEY,
    latitude DECIMAL,
    longitude DECIMAL,
    event_timestamp TIMESTAMP
);

CREATE TABLE geofence_data (
    geofence_id INT PRIMARY KEY,
    geofence_name VARCHAR,
    geofence_radius DECIMAL
);

CREATE TABLE marketing_events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR,
    event_date DATE,
    event_location_id INT
);

CREATE TABLE location_data (
  location_id INT PRIMARY KEY,
  latitude DECIMAL,
  longitude DECIMAL,
  timestamp TIMESTAMP
);
