tables.table_schema:
CREATE TABLE intersections (
    intersection_id INT PRIMARY KEY,
    intersection_name VARCHAR(255),
    city VARCHAR(255),
    wait_time_minutes INT
);

CREATE TABLE intersections (
    intersection_id INT PRIMARY KEY,
    intersection_name VARCHAR(255),
    coordinates VARCHAR(255),
    traffic_volume INT
);

CREATE TABLE wait_times (
    wait_id INT PRIMARY KEY,
    intersection_id INT,
    date_time TIMESTAMP,
    wait_time INT,
    FOREIGN KEY (intersection_id) REFERENCES intersections(intersection_id)
);

CREATE TABLE intersections (
    intersection_id INT PRIMARY KEY,
    intersection_name VARCHAR(255) NOT NULL,
    total_cars INT NOT NULL,
    average_wait_time FLOAT NOT NULL
);

CREATE TABLE traffic_lights (
    traffic_light_id INT PRIMARY KEY,
    intersection_id INT NOT NULL,
    light_color VARCHAR(50) NOT NULL,
    light_duration INT NOT NULL
);

CREATE TABLE cars (
    car_id INT PRIMARY KEY,
    intersection_id INT NOT NULL,
    car_type VARCHAR(50) NOT NULL,
    arrival_time TIMESTAMP NOT NULL
);

CREATE TABLE Intersections (
  intersection_id INT PRIMARY KEY,
  intersection_name VARCHAR(50),
  latitude DECIMAL,
  longitude DECIMAL
);

CREATE TABLE intersections (
  intersection_id INT PRIMARY KEY,
  intersection_name VARCHAR(50) NOT NULL,
  intersection_location VARCHAR(100) NOT NULL
);

CREATE TABLE vehicle_types (
  vehicle_type_id INT PRIMARY KEY,
  vehicle_type_name VARCHAR(50) NOT NULL
);

CREATE TABLE vehicles (
  vehicle_id INT PRIMARY KEY,
  vehicle_type_id INT NOT NULL,
  vehicle_name VARCHAR(50) NOT NULL,
  FOREIGN KEY (vehicle_type_id) REFERENCES vehicle_types(vehicle_type_id)
);

CREATE TABLE traffic_signals (
  signal_id INT PRIMARY KEY,
  intersection_id INT NOT NULL,
  signal_status BOOLEAN NOT NULL,
  FOREIGN KEY (intersection_id) REFERENCES intersections(intersection_id)
);
