tables.table_schema:
CREATE TABLE wildlife_migration (
    id SERIAL PRIMARY KEY,
    animal_name VARCHAR(255) NOT NULL,
    gps_coordinates GEOGRAPHY,
    migration_date DATE
);

CREATE TABLE migration_routes (
    route_id INT PRIMARY KEY,
    animal_name VARCHAR(50),
    start_location VARCHAR(100),
    end_location VARCHAR(100),
    route_distance FLOAT
);

CREATE TABLE habitats (
    habitat_id INT PRIMARY KEY,
    habitat_name VARCHAR(50),
    habitat_type VARCHAR(50),
    geographic_coordinates VARCHAR(50)
);

CREATE TABLE migration_data (
    id SERIAL PRIMARY KEY,
    animal_name VARCHAR(255),
    gps_latitude DECIMAL,
    gps_longitude DECIMAL,
    migration_date DATE
);

CREATE TABLE habitats (
    id SERIAL PRIMARY KEY,
    habitat_name VARCHAR(255),
    climate VARCHAR(100),
    elevation DECIMAL
);

CREATE TABLE animal_species (
    id SERIAL PRIMARY KEY,
    species_name VARCHAR(255),
    habitat_id INT,
    migration_status BOOLEAN
);

CREATE TABLE tracking_devices (
    id SERIAL PRIMARY KEY,
    device_name VARCHAR(255),
    battery_level DECIMAL,
    last_sync TIMESTAMP,
    animal_id INT
);
