tables.table_schema:
CREATE TABLE delivery_routes (
    route_id INT PRIMARY KEY,
    vehicle_id INT,
    route_distance DOUBLE,
    route_duration TIME
);

CREATE TABLE fleet (
    vehicle_id INT PRIMARY KEY,
    vehicle_name VARCHAR(50),
    total_capacity INT
);

CREATE TABLE routes (
  route_id INT PRIMARY KEY,
  vehicle_id INT,
  distance FLOAT,
  time_taken FLOAT
);

CREATE TABLE vehicles (
  vehicle_id INT PRIMARY KEY,
  vehicle_name VARCHAR,
  max_speed FLOAT,
  capacity FLOAT
);

CREATE TABLE stops (
  stop_id INT PRIMARY KEY,
  address VARCHAR,
  location_lat FLOAT,
  location_long FLOAT
);

CREATE TABLE route_stops (
  route_id INT,
  stop_id INT,
  sequence INT,
  PRIMARY KEY (route_id, stop_id)
);

CREATE TABLE locations (
  id INT PRIMARY KEY,
  name VARCHAR(100),
  latitude DECIMAL(10,6),
  longitude DECIMAL(10,6)
);
