CREATE TABLE FloodRiskAssessment ( 
   id INT PRIMARY KEY, 
   area_name VARCHAR(50), 
   elevation FLOAT, 
   flood_risk_level INT 
);

CREATE TABLE flood_risk_areas (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(50),
  elevation FLOAT,
  risk_level VARCHAR(20)
);

CREATE TABLE elevation_data (
  data_id INT PRIMARY KEY,
  area_id INT,
  elevation FLOAT,
  data_date DATE
);

