CREATE TABLE flood_predictions (
  prediction_id INT PRIMARY KEY,
  area_id INT,
  prediction_date DATE,
  predicted_risk_level VARCHAR(20)
);

CREATE TABLE elevation_data (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    elevation FLOAT
);

CREATE TABLE flood_zones (
    id INT PRIMARY KEY,
    zone_name VARCHAR(255),
    flood_depth FLOAT,
    risk_assessment VARCHAR(255)
);

CREATE TABLE flood_reports (
    id INT PRIMARY KEY,
    report_date DATE,
    report_text TEXT
);

CREATE TABLE flood_response_team (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(255),
    team_lead VARCHAR(255),
    team_size INT
);
