tables.table_schema:
CREATE TABLE GeofenceAreas (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(50) NOT NULL,
  polygon GEOMETRY NOT NULL
);

CREATE TABLE geofence_areas (
  id SERIAL PRIMARY KEY,
  area_name VARCHAR(255) NOT NULL,
  latitude DECIMAL(10, 8) NOT NULL,
  longitude DECIMAL(11, 8) NOT NULL
);

CREATE TABLE geofence_areas (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    latitude DECIMAL NOT NULL,
    longitude DECIMAL NOT NULL
);

CREATE TABLE events (
    id INT PRIMARY KEY,
    event_name VARCHAR(255) NOT NULL,
    event_description TEXT,
    event_type VARCHAR(50) NOT NULL
);

CREATE TABLE player_progress (
    player_id INT NOT NULL,
    event_id INT NOT NULL,
    progress_status BOOLEAN NOT NULL DEFAULT FALSE,
    PRIMARY KEY (player_id, event_id)
);

CREATE TABLE challenges (
    challenge_id INT PRIMARY KEY,
    challenge_name VARCHAR(255) NOT NULL,
    requirements TEXT,
    rewards TEXT
);

CREATE TABLE areas (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(50) NOT NULL,
  area_description TEXT,
  area_location GEOGRAPHY
);

CREATE TABLE players (
  player_id INT PRIMARY KEY,
  player_name VARCHAR(50) NOT NULL,
  player_level INT DEFAULT 1,
  player_score INT DEFAULT 0
);

CREATE TABLE events (
  event_id INT PRIMARY KEY,
  event_name VARCHAR(50) NOT NULL,
  event_type VARCHAR(30),
  event_reward INT
);

CREATE TABLE challenges (
  challenge_id INT PRIMARY KEY,
  challenge_name VARCHAR(50) NOT NULL,
  challenge_difficulty VARCHAR(20),
  challenge_reward INT
);
