tables.table_schema:
CREATE TABLE player_locations (
    player_id UUID,
    location GEOGRAPHY,
    timestamp TIMESTAMP,
    PRIMARY KEY (player_id, timestamp)
);

CREATE TABLE player_locations (
    player_id INT PRIMARY KEY,
    location_id INT,
    location_name VARCHAR(50),
    timestamp TIMESTAMP
);

CREATE TABLE player_profile (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(50),
    age INT,
    gender VARCHAR(10)
);

CREATE TABLE game_locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(50),
    region VARCHAR(50)
);

CREATE TABLE game_statistics (
    player_id INT,
    games_played INT,
    total_score INT
);

CREATE TABLE player_locations (
  player_id INT PRIMARY KEY,
  location_id INT,
  location_name TEXT,
  timestamp TIMESTAMP
);

CREATE TABLE locations (
  location_id INT PRIMARY KEY,
  location_name TEXT
);

CREATE TABLE players (
  player_id INT PRIMARY KEY,
  player_name VARCHAR(50),
  player_level INT,
  join_date DATE
);

CREATE TABLE player_activities (
  activity_id INT PRIMARY KEY,
  player_id INT,
  activity_type TEXT,
  activity_date DATE
);

CREATE TABLE activity_types (
  activity_id INT PRIMARY KEY,
  activity_name TEXT
);
