tables.table_schema:
CREATE TABLE player_leaderboards (
    player_id UUID PRIMARY KEY,
    player_name VARCHAR(255),
    region VARCHAR(50),
    achievements INT
);

CREATE TABLE player_leaderboard (
    player_id UUID PRIMARY KEY,
    player_name VARCHAR(255),
    player_region VARCHAR(50),
    player_score INT
);

CREATE TABLE region_leaderboard (
    region_id UUID PRIMARY KEY,
    region_name VARCHAR(100),
    top_player_id UUID,
    top_player_score INT
);

CREATE TABLE location (
    location_id INT PRIMARY KEY,
    region_name VARCHAR(50) NOT NULL
);

CREATE TABLE player (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(50) NOT NULL,
    region_id INT NOT NULL,
    achievements INT NOT NULL,
    FOREIGN KEY (region_id) REFERENCES location(location_id)
);

CREATE TABLE leaderboard (
    leaderboard_id INT PRIMARY KEY,
    player_id INT NOT NULL,
    rank INT NOT NULL,
    FOREIGN KEY (player_id) REFERENCES player(player_id)
);

CREATE TABLE region (
    region_id INT PRIMARY KEY,
    region_name TEXT
);

CREATE TABLE player (
    player_id INT PRIMARY KEY,
    player_name TEXT,
    player_region INT,
    FOREIGN KEY (player_region) REFERENCES region(region_id)
);

CREATE TABLE achievements (
    achievement_id INT PRIMARY KEY,
    achievement_name TEXT,
    achievement_points INT
);
