tables.table_schema:
CREATE TABLE Task ( 
 task_id UUID PRIMARY KEY, 
 task_name STRING, 
 task_description STRING, 
 quantum_state DECIMAL 
);

CREATE TABLE quantum_states (
    state_id SERIAL PRIMARY KEY,
    state_name VARCHAR(100) NOT NULL,
    computational_task VARCHAR(100) NOT NULL,
    quantum_evolution JSONB
);

CREATE TABLE computational_tasks (
    task_id SERIAL PRIMARY KEY,
    task_name VARCHAR(100) NOT NULL,
    task_description TEXT
);

CREATE TABLE evolution_tracking (
    tracking_id SERIAL PRIMARY KEY,
    state_id INT REFERENCES quantum_states(state_id),
    task_id INT REFERENCES computational_tasks(task_id),
    evolution_timestamp TIMESTAMP
);

CREATE TABLE quantum_states (
    state_id INT PRIMARY KEY,
    state_description VARCHAR(255),
    task_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE computational_tasks (
    task_id INT PRIMARY KEY,
    task_name VARCHAR(255),
    task_description TEXT,
    task_status VARCHAR(50)
);

CREATE TABLE task_dependencies (
    dependency_id INT PRIMARY KEY,
    task_id INT,
    dependency_task_id INT
);

CREATE TABLE quantum_state_logs (
    log_id INT PRIMARY KEY,
    state_id INT,
    timestamp TIMESTAMP,
    log_message TEXT
);

CREATE TABLE state_evolution (
  state_id INT PRIMARY KEY,
  task_id INT,
  quantum_state JSONB
);

CREATE TABLE tasks (
  task_id INT PRIMARY KEY,
  task_name VARCHAR(255),
  task_description TEXT
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  user_name VARCHAR(255),
  email VARCHAR(255)
);

CREATE TABLE measurements (
  measurement_id INT PRIMARY KEY,
  state_id INT,
  task_id INT,
  measurement_data JSONB
);

CREATE TABLE analysis_results (
  analysis_id INT PRIMARY KEY,
  state_id INT,
  task_id INT,
  analysis_output TEXT
);
