tables.table_schema:
CREATE TABLE quantum_evolution (
  state_id SERIAL PRIMARY KEY,
  state_description TEXT,
  time_stamp TIMESTAMPTZ
);

CREATE TABLE quantum_states (
    id INT PRIMARY KEY,
    state_name VARCHAR(255),
    wave_function TEXT,
    time_stamp TIMESTAMP
);

CREATE TABLE state_measurements (
    measurement_id INT PRIMARY KEY,
    state_id INT,
    measurement_value FLOAT,
    measurement_time TIMESTAMP,
    FOREIGN KEY (state_id) REFERENCES quantum_states(id)
);

CREATE TABLE quantum_states (
    state_id INT PRIMARY KEY,
    state_name VARCHAR(50) NOT NULL,
    state_description TEXT,
    measurement_result BOOLEAN
);

CREATE TABLE state_changes (
    change_id INT PRIMARY KEY,
    state_id INT REFERENCES quantum_states(state_id),
    change_time TIMESTAMP NOT NULL,
    change_details TEXT
);

CREATE TABLE measurements (
    measurement_id INT PRIMARY KEY,
    state_id INT REFERENCES quantum_states(state_id),
    measurement_time TIMESTAMP NOT NULL,
    measured_value FLOAT
);

CREATE TABLE quantum_states (
    state_id INT PRIMARY KEY,
    state_description TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE quantum_gates (
    gate_id INT PRIMARY KEY,
    gate_name TEXT,
    unitary_matrix TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE quantum_operators (
    operator_id INT PRIMARY KEY,
    operator_name TEXT,
    matrix_representation TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE quantum_measurements (
    measurement_id INT PRIMARY KEY,
    measurement_name TEXT,
    outcome TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE quantum_states (
    state_id INT PRIMARY KEY,
    state_name VARCHAR(50) NOT NULL,
    state_description TEXT,
    created_at TIMESTAMP
);

CREATE TABLE quantum_systems (
    system_id INT PRIMARY KEY,
    system_name VARCHAR(50) NOT NULL,
    system_description TEXT,
    created_at TIMESTAMP
);
