tables.table_schema:
CREATE TABLE QuantumCircuits (
    circuit_id INT PRIMARY KEY,
    circuit_name VARCHAR(50),
    circuit_description TEXT,
    qubit_count INT
);

CREATE TABLE Errors (
    error_id INT PRIMARY KEY,
    error_type VARCHAR(50),
    error_description TEXT,
    error_probability FLOAT
);

CREATE TABLE quantum_circuits (
    circuit_id INT PRIMARY KEY,
    circuit_name VARCHAR(255),
    circuit_description TEXT
);

CREATE TABLE quantum_circuits (
    circuit_id INT PRIMARY KEY,
    circuit_name VARCHAR(255),
    circuit_description TEXT
);

CREATE TABLE error_correction (
    correction_id INT PRIMARY KEY,
    error_description TEXT,
    gate_id INT,
    FOREIGN KEY (gate_id) REFERENCES gates(gate_id)
);

CREATE TABLE syndrome_measurement (
    measurement_id INT PRIMARY KEY,
    syndrome_result VARCHAR(10),
    correction_id INT,
    FOREIGN KEY (correction_id) REFERENCES error_correction(correction_id)
);
