tables.table_schema:
CREATE TABLE quantum_algorithm (
  algorithm_id INT PRIMARY KEY,
  algorithm_name VARCHAR(255),
  description TEXT
);

CREATE TABLE quantum_algorithms (
    algorithm_id UUID PRIMARY KEY,
    algorithm_name TEXT NOT NULL,
    algorithm_description TEXT,
    created_timestamp TIMESTAMPTZ
);

CREATE TABLE experiment_types (
    type_id SERIAL PRIMARY KEY,
    experiment_type TEXT NOT NULL,
    experiment_description TEXT
);

CREATE TABLE experiments (
    experiment_id UUID PRIMARY KEY,
    experiment_name TEXT NOT NULL,
    experiment_type_id INTEGER REFERENCES experiment_types(type_id),
    experiment_date DATE
);

CREATE TABLE algorithm_experiments (
    algorithm_id UUID REFERENCES quantum_algorithms(algorithm_id),
    experiment_id UUID REFERENCES experiments(experiment_id)
);

CREATE TABLE quantum_algorithms (
	algorithm_id INT PRIMARY KEY,
	algorithm_name VARCHAR(50) NOT NULL,
	release_date DATE,
	description TEXT
);

CREATE TABLE researchers (
	researcher_id INT PRIMARY KEY,
	researcher_name VARCHAR(50) NOT NULL
);

CREATE TABLE algorithm_metadata (
	algorithm_id INT PRIMARY KEY,
	purpose TEXT,
	complexity VARCHAR(50)
);
