tables.table_schema:
CREATE TABLE error_correction_codes (
    code_id INT PRIMARY KEY,
    code_name VARCHAR(50) NOT NULL,
    code_description TEXT
);

CREATE TABLE syndromes (
    syndrome_id INT PRIMARY KEY,
    syndrome_code_id INT,
    syndrome_description TEXT,
    FOREIGN KEY (syndrome_code_id) REFERENCES error_correction_codes(code_id)
);

CREATE TABLE error_correction_codes (
    code_id INT PRIMARY KEY,
    code_name TEXT,
    properties JSONB
);

CREATE TABLE syndromes (
    syndrome_id INT PRIMARY KEY,
    syndrome_name TEXT,
    properties JSONB
);

CREATE TABLE codes_syndromes (
    code_id INT REFERENCES error_correction_codes(code_id),
    syndrome_id INT REFERENCES syndromes(syndrome_id)
);

CREATE TABLE Quantum_Error_Correction_Codes (
    code_id SERIAL PRIMARY KEY,
    code_name VARCHAR(50) NOT NULL,
    code_description TEXT
);

CREATE TABLE Code_Syndromes (
    syndrome_id SERIAL PRIMARY KEY,
    syndrome_code_id INT NOT NULL,
    syndrome_name VARCHAR(50) NOT NULL,
    FOREIGN KEY (syndrome_code_id) REFERENCES Quantum_Error_Correction_Codes(code_id)
);

CREATE TABLE Syndrome_Errors (
    error_id SERIAL PRIMARY KEY,
    error_syndrome_id INT NOT NULL,
    error_description TEXT,
    FOREIGN KEY (error_syndrome_id) REFERENCES Code_Syndromes(syndrome_id)
);

CREATE TABLE QuantumErrorCorrectionCodes (
    code_id INT PRIMARY KEY,
    code_name VARCHAR(255),
    code_description TEXT
);

CREATE TABLE QuantumSyndromes (
    syndrome_id INT PRIMARY KEY,
    syndrome_name VARCHAR(255),
    syndrome_description TEXT
);
