tables.table_schema:
CREATE TABLE convergence_behavior (
    experiment_id INT PRIMARY KEY,
    algorithm_name VARCHAR(255),
    optimization_goal VARCHAR(255),
    convergence_rate DECIMAL
);

CREATE TABLE quantum_machine_learning_optimization (
    optimization_id INT PRIMARY KEY,
    optimization_algorithm VARCHAR(50),
    convergence_rate DECIMAL
);

CREATE TABLE QuantumComputers (
    computer_id INT PRIMARY KEY,
    computer_name VARCHAR(50) NOT NULL,
    qubit_count INT NOT NULL,
    manufacturer VARCHAR(50) NOT NULL
);
