tables.table_schema:
CREATE TABLE secure_key_exchange (
    user_id INT PRIMARY KEY,
    public_key BYTEA NOT NULL,
    private_key BYTEA NOT NULL
);

CREATE TABLE key_exchange (
    key_id INT PRIMARY KEY,
    public_key TEXT,
    private_key TEXT
);

CREATE TABLE key_logs (
    log_id INT PRIMARY KEY,
    key_id INT,
    log_time TIMESTAMP,
    log_message TEXT
);

CREATE TABLE key_exchange_protocol (
    id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    encryption_key VARCHAR(256) NOT NULL,
    date_created TIMESTAMP
);

CREATE TABLE user_credentials (
    id SERIAL PRIMARY KEY,
    user_id INT UNIQUE NOT NULL,
    username VARCHAR(50) UNIQUE NOT NULL,
    password_hash VARCHAR(256) NOT NULL
);

CREATE TABLE access_logs (
    id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    access_time TIMESTAMP NOT NULL
);

CREATE TABLE encryption_keys (
    key_id UUID PRIMARY KEY,
    key_value BYTEA NOT NULL,
    user_id INT NOT NULL,
    created_at TIMESTAMP
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(50) NOT NULL
);

CREATE TABLE sessions (
    session_id UUID PRIMARY KEY,
    user_id INT NOT NULL,
    session_start TIMESTAMP,
    session_end TIMESTAMP
);

CREATE TABLE permissions (
    permission_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    permission_type VARCHAR(20) NOT NULL
);

CREATE TABLE encryption_keys (
    id INT PRIMARY KEY,
    key_name VARCHAR(50) NOT NULL,
    key_value VARCHAR(100) NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password_hash VARCHAR(255) NOT NULL
);

CREATE TABLE key_exchanges (
    exchange_id INT PRIMARY KEY,
    key_type VARCHAR(50) NOT NULL,
    exchange_data VARCHAR(100) NOT NULL
);

CREATE TABLE key_permissions (
    permission_id INT PRIMARY KEY,
    key_id INT NOT NULL,
    user_id INT NOT NULL,
    permission_type VARCHAR(20) NOT NULL
);

CREATE TABLE key_usage_logs (
    log_id INT PRIMARY KEY,
    key_id INT NOT NULL,
    user_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);
