tables.table_schema:
CREATE TABLE software_tools (
	tool_id UUID PRIMARY KEY,
	tool_name VARCHAR(100),
	tool_description TEXT,
	tool_category VARCHAR(50)
);

CREATE TABLE tools (
    tool_id INT PRIMARY KEY,
    tool_name VARCHAR(50) NOT NULL,
    version FLOAT,
    description TEXT
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50) NOT NULL,
    institution VARCHAR(100) NOT NULL,
    field_of_study VARCHAR(50)
);

CREATE TABLE workflow (
    workflow_id INT PRIMARY KEY,
    workflow_name VARCHAR(50) NOT NULL,
    researcher_id INT,
    FOREIGN KEY (researcher_id) REFERENCES researchers(researcher_id)
);

CREATE TABLE Researchers (
	researcher_id INT PRIMARY KEY,
	researcher_name VARCHAR(255)
);

CREATE TABLE SoftwareTools (
	tool_id INT PRIMARY KEY,
	tool_name VARCHAR(255),
	tool_description TEXT,
	tool_language VARCHAR(50)
);

CREATE TABLE QuantumSimulationWorkflows (
	workflow_id INT PRIMARY KEY,
	workflow_name VARCHAR(255),
	workflow_description TEXT,
	workflow_status VARCHAR(20)
);

CREATE TABLE ResearcherTools (
	researcher_id INT,
	tool_id INT,
	FOREIGN KEY (researcher_id) REFERENCES Researchers(researcher_id),
	FOREIGN KEY (tool_id) REFERENCES SoftwareTools(tool_id)
);
