tables.table_schema:
CREATE TABLE quantum_computers (
    computer_id UUID PRIMARY KEY,
    computer_name VARCHAR(50),
    manufacturer VARCHAR(50),
    qubit_count INT
);

CREATE TABLE quantum_gates (
    gate_id UUID PRIMARY KEY,
    gate_name VARCHAR(50),
    gate_type VARCHAR(50),
    error_rate FLOAT
);

CREATE TABLE quantum_simulations (
    simulation_id UUID PRIMARY KEY,
    simulation_name VARCHAR(50),
    simulation_date DATE,
    simulation_results JSON
);

CREATE TABLE circuits (
    circuit_id INT PRIMARY KEY,
    circuit_name VARCHAR(100),
    circuit_description TEXT
);
