tables.table_schema:
CREATE TABLE research_publications (
    publication_id INT PRIMARY KEY,
    publication_title TEXT,
    publication_author TEXT,
    publication_date DATE
);

CREATE TABLE research_topics (
    topic_id INT PRIMARY KEY,
    topic_name TEXT
);

CREATE TABLE publication_topics (
    publication_id INT,
    topic_id INT,
    FOREIGN KEY (publication_id) REFERENCES research_publications(publication_id),
    FOREIGN KEY (topic_id) REFERENCES research_topics(topic_id)
);

CREATE TABLE authors (
    id SERIAL PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    affiliation VARCHAR(255),
    email VARCHAR(255)
);

CREATE TABLE topics (
    id SERIAL PRIMARY KEY,
    topic_name VARCHAR(255) UNIQUE
);

CREATE TABLE publication_topic (
    publication_id INT REFERENCES publications(id),
    topic_id INT REFERENCES topics(id),
    PRIMARY KEY (publication_id, topic_id)
);

CREATE TABLE research_publications (
	publication_id INT PRIMARY KEY,
	author_name VARCHAR,
	publication_title VARCHAR,
	publication_date DATE
);

CREATE TABLE authors (
	author_id INT PRIMARY KEY,
	author_name VARCHAR,
	affiliation VARCHAR
);

CREATE TABLE keywords (
	keyword_id INT PRIMARY KEY,
	keyword VARCHAR
);

CREATE TABLE citations (
	citation_id INT PRIMARY KEY,
	publication_id INT,
	citation_text TEXT,
	FOREIGN KEY (publication_id) REFERENCES research_publications(publication_id)
);
