CREATE TABLE error_syndromes (
    syndrome_id INT PRIMARY KEY,
    syndrome_description TEXT
);

CREATE TABLE error_corrections (
    correction_id INT PRIMARY KEY,
    syndrome_id INT,
    correction_operation TEXT,
    FOREIGN KEY (syndrome_id) REFERENCES error_syndromes(syndrome_id)
);

CREATE TABLE qubit_states (
    id SERIAL PRIMARY KEY,
    qubit_state VARCHAR(255) NOT NULL,
    error_syndrome VARCHAR(255) NOT NULL
);

CREATE TABLE error_corrections (
    id SERIAL PRIMARY KEY,
    error_correction_op VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE qubit_errors (
    id SERIAL PRIMARY KEY,
    qubit_id INT REFERENCES qubit_states(id),
