);

CREATE TABLE error_syndromes (
  syndrome_id INT PRIMARY KEY,
  syndrome_description TEXT
);

CREATE TABLE error_corrections (
  correction_id INT PRIMARY KEY,
  correction_description TEXT
);

CREATE TABLE qubit_states (
  state_id INT PRIMARY KEY,
  state_description TEXT
);

CREATE TABLE syndrome_corrections (
  syndrome_id INT,
  correction_id INT,
  FOREIGN KEY (syndrome_id) REFERENCES error_syndromes(syndrome_id),
  FOREIGN KEY (correction_id) REFERENCES error_corrections(correction_id)
);

CREATE TABLE state_corrections (
  state_id INT,
  correction_id INT,
  FOREIGN KEY (state_id) REFERENCES qubit_states(state_id),
  FOREIGN KEY (correction_id) REFERENCES error_corrections(correction_id)
);
