tables.table_schema:
CREATE TABLE error_detection_circuits (
    circuit_id INT PRIMARY KEY,
    circuit_name VARCHAR(255),
    circuit_type VARCHAR(100),
    detection_method VARCHAR(100)
);

CREATE TABLE error_correction_circuits (
    circuit_id INT PRIMARY KEY,
    circuit_name VARCHAR(255),
    circuit_type VARCHAR(100),
    correction_method VARCHAR(100)
);

CREATE TABLE ErrorDetectionCircuits (
 id INT PRIMARY KEY,
 name VARCHAR(255),
 description TEXT
);

CREATE TABLE ErrorCorrectionCircuits (
 id INT PRIMARY KEY,
 name VARCHAR(255),
 description TEXT
);

CREATE TABLE CircuitConnections (
 id INT PRIMARY KEY,
 error_detection_id INT,
 error_correction_id INT
);

CREATE TABLE CircuitLogs (
 id INT PRIMARY KEY,
 circuit_id INT,
 log_date TIMESTAMP,
 log_message TEXT
);

CREATE TABLE ErrorDetectionCircuits (
  circuit_id INT PRIMARY KEY,
  circuit_name VARCHAR(255) NOT NULL,
  circuit_type VARCHAR(50) NOT NULL,
  error_threshold FLOAT NOT NULL
);

CREATE TABLE ErrorCorrectionCircuits (
  circuit_id INT PRIMARY KEY,
  circuit_name VARCHAR(255) NOT NULL,
  circuit_type VARCHAR(50) NOT NULL,
  correction_algorithm VARCHAR(100) NOT NULL
);

CREATE TABLE CircuitDetails (
  circuit_id INT PRIMARY KEY,
  circuit_description TEXT,
  manufacturer VARCHAR(100) NOT NULL,
  manufacturing_date DATE
);

CREATE TABLE CircuitConnections (
  connection_id INT PRIMARY KEY,
  source_circuit_id INT NOT NULL,
  destination_circuit_id INT NOT NULL,
  connection_type VARCHAR(50) NOT NULL
);

CREATE TABLE CircuitLogs (
  log_id INT PRIMARY KEY,
  circuit_id INT NOT NULL,
  log_timestamp TIMESTAMP NOT NULL,
  log_message TEXT
);
