tables.table_schema:
CREATE TABLE error_correction_jobs (
    job_id INT PRIMARY KEY,
    quantum_device_id INT,
    error_type VARCHAR(50),
    status VARCHAR(50)
);

CREATE TABLE quantum_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50),
    manufacturer VARCHAR(50),
    total_qubits INT
);

CREATE TABLE resource_allocation (
    id SERIAL PRIMARY KEY,
    device_id INT,
    allocation_date DATE,
    allocated_cores INT
);

CREATE TABLE error_correction_resources (
    resource_id UUID PRIMARY KEY,
    resource_name VARCHAR(255) NOT NULL,
    resource_type VARCHAR(50) NOT NULL,
    allocated_time TIMESTAMP NOT NULL
);

CREATE TABLE quantum_devices (
    device_id UUID PRIMARY KEY,
    device_name VARCHAR(255) NOT NULL,
    device_type VARCHAR(50) NOT NULL,
    device_location VARCHAR(255) NOT NULL
);

CREATE TABLE resource_allocation (
    allocation_id UUID PRIMARY KEY,
    resource_id UUID NOT NULL,
    device_id UUID NOT NULL,
    start_time TIMESTAMP NOT NULL,
    end_time TIMESTAMP NOT NULL
);

CREATE TABLE error_correction_tasks (
    task_id UUID PRIMARY KEY,
    task_description TEXT NOT NULL,
    task_status VARCHAR(20) NOT NULL
);

CREATE TABLE error_correction_results (
    result_id UUID PRIMARY KEY,
    task_id UUID NOT NULL,
    error_type VARCHAR(50) NOT NULL,
    error_details TEXT NOT NULL
);
