tables.table_schema:
CREATE TABLE QuantumErrorCorrection (
    error_id SERIAL PRIMARY KEY,
    error_type VARCHAR(50) NOT NULL,
    error_description TEXT
);

CREATE TABLE QuantumTransmissionLogs (
    log_id SERIAL PRIMARY KEY,
    transmission_date TIMESTAMP NOT NULL,
    sender_address VARCHAR(100) NOT NULL,
    receiver_address VARCHAR(100) NOT NULL,
    transmission_status BOOLEAN
);

CREATE TABLE ErrorCorrection (
  id INT PRIMARY KEY,
  quantum_data TEXT,
  error_code VARCHAR(50),
  correction_method VARCHAR(50)
);

CREATE TABLE QuantumDevices (
  device_id SERIAL PRIMARY KEY,
  device_name VARCHAR(100),
  location VARCHAR(100),
  status VARCHAR(20)
);

CREATE TABLE ErrorLogs (
  log_id SERIAL PRIMARY KEY,
  error_time TIMESTAMP,
  error_description TEXT,
  solution TEXT
);

CREATE TABLE error_correction_processes (
    process_id INT PRIMARY KEY,
    quantum_data VARCHAR(255),
    error_code VARCHAR(50),
    correction_method VARCHAR(50)
);

CREATE TABLE error_logs (
    log_id INT PRIMARY KEY,
    process_id INT,
    error_message TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE error_statistics (
    statistic_id INT PRIMARY KEY,
    process_id INT,
    error_count INT,
    error_type VARCHAR(50)
);

CREATE TABLE quantum_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50),
    operational_status BOOL,
    location VARCHAR(100)
);

CREATE TABLE QuantumNetworkNodes (
    node_id SERIAL PRIMARY KEY,
    node_name VARCHAR(50) NOT NULL,
    node_location VARCHAR(100) NOT NULL
);

CREATE TABLE QuantumDataTransmissionLogs (
    log_id SERIAL PRIMARY KEY,
    transmission_date TIMESTAMP NOT NULL,
    data_size INT NOT NULL,
    success BOOLEAN NOT NULL
);

CREATE TABLE QuantumErrorCorrectionCodes (
    code_id SERIAL PRIMARY KEY,
    code_name VARCHAR(50) NOT NULL,
    code_type VARCHAR(50) NOT NULL
);

CREATE TABLE QuantumDataReceived (
    receive_id SERIAL PRIMARY KEY,
    receive_date TIMESTAMP NOT NULL,
    received_data BYTEA NOT NULL,
    error_corrected BOOLEAN NOT NULL
);

CREATE TABLE QuantumErrorCorrectionHistory (
    history_id SERIAL PRIMARY KEY,
    error_correction_type VARCHAR(50) NOT NULL,
    correction_date TIMESTAMP NOT NULL,
    success BOOLEAN NOT NULL
);
