tables.table_schema:
CREATE TABLE quantum_users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE,
    password_hash VARCHAR(100) NOT NULL
);

CREATE TABLE quantum_resources (
    resource_id UUID PRIMARY KEY,
    resource_name VARCHAR(50) NOT NULL,
    access_level INT,
    owner_id UUID REFERENCES quantum_users(user_id)
);

CREATE TABLE quantum_hardware (
    hardware_id INT PRIMARY KEY,
    hardware_name VARCHAR(50) UNIQUE,
    location VARCHAR(50),
    status VARCHAR(20)
);

CREATE TABLE user_roles (
  user_id INT PRIMARY KEY,
  role_name VARCHAR(50) NOT NULL
);

CREATE TABLE quantum_devices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(50) NOT NULL,
  device_type VARCHAR(50) NOT NULL
);

CREATE TABLE user_device_access (
  user_id INT,
  device_id INT,
  FOREIGN KEY (user_id) REFERENCES user_roles(user_id),
  FOREIGN KEY (device_id) REFERENCES quantum_devices(device_id)
);

CREATE TABLE device_permissions (
  device_id INT,
  permission_name VARCHAR(50) NOT NULL,
  FOREIGN KEY (device_id) REFERENCES quantum_devices(device_id)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(50) NOT NULL
);

CREATE TABLE roles (
    role_id INT PRIMARY KEY,
    role_name VARCHAR(50) NOT NULL
);

CREATE TABLE user_roles (
    user_id INT,
    role_id INT,
    FOREIGN KEY (user_id) REFERENCES users(user_id),
    FOREIGN KEY (role_id) REFERENCES roles(role_id)
);

CREATE TABLE resources (
    resource_id INT PRIMARY KEY,
    resource_name VARCHAR(50) NOT NULL
);
