tables.table_schema:
CREATE TABLE quantum_hardware_components (
    component_id SERIAL PRIMARY KEY,
    component_name VARCHAR(50) NOT NULL,
    status VARCHAR(20) NOT NULL,
    location VARCHAR(50) NOT NULL
);

CREATE TABLE quantum_hardware_component (
  component_id UUID PRIMARY KEY,
  component_name VARCHAR(255) NOT NULL,
  availability BOOLEAN
);

CREATE TABLE component_status (
  status_id UUID PRIMARY KEY,
  component_id UUID REFERENCES quantum_hardware_component(component_id),
  status VARCHAR(50) NOT NULL,
  last_checked TIMESTAMPTZ
);

CREATE TABLE component_location (
  location_id UUID PRIMARY KEY,
  component_id UUID REFERENCES quantum_hardware_component(component_id),
  location_name VARCHAR(100),
  address VARCHAR(255)
);

CREATE TABLE quantum_components (
    component_id INT PRIMARY KEY,
    component_name VARCHAR(255),
    component_type VARCHAR(50),
    component_status VARCHAR(50)
);

CREATE TABLE component_status (
    status_id INT PRIMARY KEY,
    status_name VARCHAR(50),
    status_description TEXT
);

CREATE TABLE component_history (
    history_id INT PRIMARY KEY,
    component_id INT,
    status_id INT,
    history_date TIMESTAMP
);

CREATE TABLE component_status (
    status_id INT PRIMARY KEY,
    component_id INT,
    status VARCHAR(20),
    last_checked TIMESTAMP
);
