tables.table_schema:
CREATE TABLE QuantumDevices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(255),
    user_id INT,
    last_updated TIMESTAMP
);

CREATE TABLE quantum_hardware_data (
    id INT PRIMARY KEY,
    user_id INT,
    device_name VARCHAR,
    quantum_bits INT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR,
    email VARCHAR,
    role VARCHAR
);

CREATE TABLE quantum_hardware (
    id INT PRIMARY KEY,
    hardware_name VARCHAR(255) NOT NULL,
    owner_id INT,
    last_updated TIMESTAMP
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    username VARCHAR(255) UNIQUE NOT NULL,
    email VARCHAR(255) UNIQUE NOT NULL
);

CREATE TABLE user_access (
    user_id INT,
    hardware_id INT,
    FOREIGN KEY (user_id) REFERENCES users(id),
    FOREIGN KEY (hardware_id) REFERENCES quantum_hardware(id)
);

CREATE TABLE hardware_logs (
    log_id INT PRIMARY KEY,
    hardware_id INT,
    action VARCHAR(255) NOT NULL,
    timestamp TIMESTAMP
);

CREATE TABLE quantum_hardware (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    location VARCHAR(50),
    description TEXT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(50)
);

CREATE TABLE access_rights (
    user_id INT,
    table_name VARCHAR(50),
    read_access BOOLEAN,
    write_access BOOLEAN
);

CREATE TABLE experiment_data (
    data_id SERIAL PRIMARY KEY,
    experiment_id INT,
    data_name VARCHAR(50),
    data_value TEXT
);
