tables.table_schema:
CREATE TABLE quantum_communication_data (
	id INT PRIMARY KEY,
	message TEXT,
	encrypted_message BLOB
);

CREATE TABLE encryption_keys (
	id INT PRIMARY KEY,
	key_name TEXT,
	encryption_key BLOB
);

CREATE TABLE QuantumCommunicationData (
  data_id INT PRIMARY KEY,
  quantum_message VARCHAR(255) NOT NULL,
  encryption_key VARCHAR(50) NOT NULL,
  timestamp TIMESTAMP NOT NULL
);

CREATE TABLE quantum_messages (
    message_id SERIAL PRIMARY KEY,
    sender VARCHAR(50) NOT NULL,
    recipient VARCHAR(50) NOT NULL,
    message_body TEXT NOT NULL
);

CREATE TABLE encryption_keys (
    key_id SERIAL PRIMARY KEY,
    key_name VARCHAR(100) NOT NULL,
    key_value VARCHAR(200) NOT NULL
);

CREATE TABLE communication_logs (
    log_id SERIAL PRIMARY KEY,
    message_id INT NOT NULL,
    encryption_key_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE message_attachments (
    attachment_id SERIAL PRIMARY KEY,
    message_id INT NOT NULL,
    attachment_name VARCHAR(100) NOT NULL,
    attachment_data BYTEA NOT NULL
);

CREATE TABLE encrypted_quantum_keys (
    id INT PRIMARY KEY,
    key_value VARCHAR(255) NOT NULL
);

CREATE TABLE quantum_devices (
    device_id INT PRIMARY KEY,
    device_location VARCHAR(100) NOT NULL,
    quantum_level VARCHAR(20) NOT NULL
);

CREATE TABLE quantum_network (
    network_id INT PRIMARY KEY,
    network_name VARCHAR(50) NOT NULL
);
