tables.table_schema:
CREATE TABLE access_control_permissions (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(255),
    permission_level INT
);

CREATE TABLE access_control (
    id INT PRIMARY KEY,
    user_id INT,
    permission_level INT
);

CREATE TABLE quantum_data (
    id INT PRIMARY KEY,
    data_type VARCHAR(50),
    data_content TEXT
);

CREATE TABLE quantum_users(
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(50) NOT NULL,
    role VARCHAR(20) NOT NULL
);

CREATE TABLE quantum_data(
    data_id UUID PRIMARY KEY,
    data_name VARCHAR(100) NOT NULL,
    data_content TEXT NOT NULL,
    user_id UUID REFERENCES quantum_users(user_id)
);

CREATE TABLE access_control(
    access_id UUID PRIMARY KEY,
    user_id UUID REFERENCES quantum_users(user_id),
    data_id UUID REFERENCES quantum_data(data_id),
    permission VARCHAR(10) NOT NULL
);

CREATE TABLE quantum_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(50) NOT NULL
);

CREATE TABLE quantum_roles (
    role_id INT PRIMARY KEY,
    role_name VARCHAR(50) NOT NULL
);

CREATE TABLE quantum_permissions (
    permission_id INT PRIMARY KEY,
    permission_name VARCHAR(50) NOT NULL
);

CREATE TABLE user_roles (
    user_id INT REFERENCES quantum_users(user_id),
    role_id INT REFERENCES quantum_roles(role_id),
    PRIMARY KEY (user_id, role_id)
);

CREATE TABLE access_control (
    user_id INT PRIMARY KEY,
    role VARCHAR(50) NOT NULL,
    permission VARCHAR(100) NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(100) NOT NULL
);

CREATE TABLE logs (
    log_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    action VARCHAR(50) NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE data_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(50) NOT NULL,
    quantum_data BLOB NOT NULL
);

CREATE TABLE permissions (
    permission_id INT PRIMARY KEY,
    permission_name VARCHAR(50) NOT NULL
);
