tables.table_schema:
CREATE TABLE encryption_keys (
    key_id UUID PRIMARY KEY,
    key_value BYTEA NOT NULL,
    created_at TIMESTAMP NOT NULL
);
CREATE TABLE quantum_messages (
    message_id UUID PRIMARY KEY,
    encrypted_message BYTEA NOT NULL,
    decryption_status BOOLEAN NOT NULL
);

CREATE TABLE encryption_keys (
    id INT PRIMARY KEY,
    key_name VARCHAR(50) NOT NULL,
    key_value BLOB NOT NULL
);

CREATE TABLE quantum_messages (
    message_id INT PRIMARY KEY,
    message_text TEXT NOT NULL,
    encryption_key_id INT,
    FOREIGN KEY (encryption_key_id) REFERENCES encryption_keys(id)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(50) NOT NULL,
    password_hash VARCHAR(100) NOT NULL
);

CREATE TABLE EncryptionKeys (
    key_id INT PRIMARY KEY,
    key_value VARCHAR(255) NOT NULL,
    encryption_algorithm VARCHAR(50) NOT NULL
);

CREATE TABLE CommunicationMessages (
    message_id INT PRIMARY KEY,
    message_content TEXT NOT NULL,
    encryption_key_id INT REFERENCES EncryptionKeys(key_id)
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL
);

CREATE TABLE UserPermissions (
    permission_id INT PRIMARY KEY,
    user_id INT REFERENCES Users(user_id),
    encryption_key_id INT REFERENCES EncryptionKeys(key_id)
);

CREATE TABLE encryption_keys (
