tables.table_schema:
CREATE TABLE quantum_communication_protocols (
    protocol_id UUID PRIMARY KEY,
    protocol_name VARCHAR(50) NOT NULL,
    description TEXT,
    quantum_bit_rate INT
);

CREATE TABLE QuantumProtocols (
    protocol_id INT PRIMARY KEY,
    protocol_name VARCHAR(255),
    description TEXT
);

CREATE TABLE ProtocolData (
    data_id INT PRIMARY KEY,
    protocol_id INT,
    data_content JSONB
    FOREIGN KEY (protocol_id) REFERENCES QuantumProtocols(protocol_id)
);

CREATE TABLE quantum_protocols (
    protocol_id INT PRIMARY KEY,
    protocol_name VARCHAR(50),
    description TEXT,
    creation_date DATE
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(50),
    password VARCHAR(50)
);
